function generatePassword() {
    event.preventDefault();
    var length = Math.floor(4 * Math.random()) + 8;
    document.getElementById("password").value = makePassword(length);
}
function makePassword(length) {
    for (
        var text = "",
            possible =
                "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789",
            i = 0;
        i < length;
        i++
    )
        text += possible.charAt(Math.floor(Math.random() * possible.length));
    return text;
}
function submitAddForm() {
    event.preventDefault();
    let form = document.getElementById("addBlogForm"),
        content,
        text = CKEDITOR.instances["editor contentText"].getData();
    (document.getElementById("contentText").value = text),
        (form.action = "/blogs"),
        (document.getElementById("language").disabled = !1),
        (document.getElementById("tags").disabled = !1),
        form.submit();
}
function submitEditForm() {
    event.preventDefault();
    let lang = document.getElementById("language").value,
        id = document.getElementById("idValue").value,
        form = document.getElementById("editBlogForm"),
        content,
        text = CKEDITOR.instances["editor contentText"].getData();
    (document.getElementById("contentText").value = text),
        (form.action = "/blogs/" + id + "/" + lang),
        form.submit();
}
function changeSiteLanguage(value) {
    document.getElementById("languageForm").submit();
    window.location = "http://smartlab.ba/?lang=" + value;
}
